"use strict";

var _require = require('humps'),
    pascalize = _require.pascalize;

module.exports = function itemNodeId(id, locale, entitiesRepo) {
  if (!id) {
    return null;
  }

  var entity = entitiesRepo.findEntity('item', id);

  if (!entity) {
    return null;
  }

  var type = pascalize(entity.itemType.apiKey);
  return "DatoCms".concat(type, "-").concat(entity.id, "-").concat(locale);
};